import { Component, OnInit, NgZone } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { CrudService } from 'src/app/service/crud.service';
import { ActivatedRoute } from '@angular/router';
@Component({
selector: 'app-update-house',
templateUrl: './update-house.component.html',

styleUrls: ['./update-house.component.css']
})
export class UpdateHouseComponent implements OnInit {
Form: FormGroup;

constructor(
public formBuilder: FormBuilder,
private router: Router,
private ngZone: NgZone,
private crudService: CrudService,
private route: ActivatedRoute
) {
this.Form = this.formBuilder.group({
isbn: [''],
title: [''],
author: [''],
description: [''],
published_year: [''],
publisher: ['']
})
}
ngOnInit(): void { 
  // Accessing the parameter value from the URL
  const houseId = this.route.snapshot.paramMap.get('id');
  console.log('House ID:', houseId);}
onSubmit(): void {

    this.crudService.UpdateHouse(this.route.snapshot.paramMap.get('id'), this.Form.value)
      .subscribe(() => {
        console.log('House updated successfully');
        this.ngZone.run(() => this.router.navigateByUrl('/houses-list'));
      });

}
}
<div class="row justive-content-center mt-5">
    <div class="col-md-4">
    <form [formGroup]="Form" (ngSubmit)="onSubmit()">
    <div class="form-group">
    <label>ISBN</label>
    <input class="form-control" type="text"
    formControlName="isbn" required>
    </div>
    <div class="form-group">
        <label>Census Taker's Name</label>
        <input class="form-control" type="text"
        formControlName="censustakername" required>
        </div>
        <div class="form-group">
        <label># of People in House</label>
        <input class="form-control" type="text"
        formControlName="numberofpeople" required>
        </div>
        <div class="form-group">
        <label>City</label>
        <input class="form-control" type="text"
        formControlName="city" required>
        </div>
        <div class="form-group">
        <label>State</label>
        <input class="form-control" type="text"
        formControlName="state" required>
        </div>
        <div class="form-group">
        <label>Zip Code</label>
        <input class="form-control" type="text"
        formControlName="zip" required>
        </div>
        <div class="form-group">
        <button class="btn btn-primary btn-block"
        type="submit">Update House</button>
        </div>
        </form>
        </div>
        </div>
