import { Component, OnInit, NgZone } from '@angular/core';
import { FormBuilder, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';
import { CrudService } from 'src/app/service/crud.service';
@Component({
selector: 'app-update-book',
templateUrl: './update-book.component.html',
styleUrls: ['./update-book.component.css']
})
export class UpdateBookComponent implements OnInit {
bookForm: FormGroup;
bookId: any;
constructor(
public formBuilder: FormBuilder,
private router: Router,
private ngZone: NgZone,
private crudService: CrudService
) {
this.bookForm = this.formBuilder.group({
isbn: [''],
title: [''],
author: [''],
description: [''],
published_year: [''],
publisher: ['']
})
}
ngOnInit(): void { }
onSubmit(): any {
    this.crudService.UpdateBook(this.bookId, this.bookForm.value)
    .subscribe(() => {
      console.log('Book updated successfully');
      this.ngZone.run(() => this.router.navigateByUrl('/books-list'));
    });

this.ngZone.run(() => this.router.navigateByUrl('/books-list'));
}
}
<div class="row justive-content-center mt-5">
    <div class="col-md-4">
    <form [formGroup]="bookForm" (ngSubmit)="onSubmit()">
    <div class="form-group">
    <label>ISBN</label>
    <input class="form-control" type="text"
    formControlName="isbn" required>
    </div>
    <div class="form-group">
        <label>Title</label>
        <input class="form-control" type="text"
        formControlName="title" required>
        </div>
        <div class="form-group">
        <label>Author</label>
        <input class="form-control" type="text"
        formControlName="author" required>
        </div>
        <div class="form-group">
        <label>Description</label>
        <input class="form-control" type="text"
        formControlName="description" required>
        </div>
        <div class="form-group">
        <label>Published Year</label>
        <input class="form-control" type="text"
        formControlName="published_year" required>
        </div>
        <div class="form-group">
        <label>Publisher</label>
        <input class="form-control" type="text"
        formControlName="publisher" required>
        </div>
        <div >
        <button class="btn btn-primary btn-block"
        type="submit">Update Book</button>
        </div>
        </form>
        </div>
        </div>
